<script>
    import {Collapse} from "flowbite";

    document.addEventListener("DOMContentLoaded", function () {
        const collapseButton = document.querySelector("[data-collapse-toggle='full-width-megamenu-mobile']");
        const collapseElement = document.getElementById("full-width-megamenu-mobile");
        const collapse = new Collapse(collapseElement);


        collapseButton.addEventListener("click", function (event) {
            event.preventDefault();
            collapse.toggle();
            const chevron = collapseButton.querySelector("svg");
            chevron.classList.toggle("rotate-180");
        });

        const menuItems = collapseElement.querySelectorAll("a");
        menuItems.forEach(item => {
            item.addEventListener("click", function (event) {
                event.stopPropagation();
            });
        });
    });
</script>
